import React from 'react'

class BeachShow extends React.Component{
    state={
        reviews: false
    }

    renderReviews=(event)=>{event.preventDefault()
        {this.state.reviews}
    }

    handleReview=(event)=>{
        event.preventDefault()
        this.state.reviews===false ?this.setState({reviews: true}):  this.setState({reviews: false})
    }

    renderReviewButton=()=>{
        {this.state.reviews === false? <button onClick={this.handleReview}>Reviews</button>:<button onClick={this.handleReview}>Beach</button>}
    }
    render(){
        return(
            <div className="BeachDiv">
                <div>
                    <h1>{this.props.name}</h1>
                    <h3>{this.props.location}</h3>
                    <img src={this.props.picture} alt={this.props.name + ": " + this.props.location}/> 
                    {this.renderReviewButton()}               
                    </div>
            </div>
        )
    }
}

export default BeachShow
