import React from 'react'

class UserProfile extends React.Component{
    userReviews=()=>{this.props.reviews.filter(review => review.user_id === this.props.user.id)}
    render(){
        return(
            <div className="profile">
                <h3>{this.props.user.name}</h3>
                <h3>{this.props.user.username}</h3>
                <h5>Age: {this.props.user.age}</h5>
                <h5>Number of Reviews:{this.userReviews().length()}</h5>
                <h5>Total Upvotes: {sum(this.userReviews().map(review => review.upvote))}</h5>
            </div>
        )

    }
}
export default UserProfile
