import React from 'react'
import BeachReview from './BeachReview'

class BeachShow extends React.Component{
    state={
        reviews: false,
        toggleReviewForm: false,
        comment: "",
        picture: "",
        airpollution: null,
        waterpollution: null,
        plasticpollution: null,
        foottraffick:null,
        noisepollution: null,
        lightpollution:null,
        showForm:true
    }

    commentText=(event)=>{
        event.preventDefault()
        this.setState({...this.state, comment: event.target.value})
    }

    imageURL=(event)=>{
        event.preventDefault()
        this.setState({...this.state, comment: event.target.value})
    }

    showForm=(event)=>{
        event.preventDefault()
        this.state.showForm===true?
        this.setState({...this.state, showForm: false}):
        this.setState({...this.state, showForm: true})
    }

    showFormRender=()=>{
        return(this.state.showForm === true ? "Back": "Create New Review")
    }

    onSubmit=(event)=>{
        event.preventDefault()
        console.log(this.state)
    }

    airPollutionSelect=(event)=>{
        event.preventDefault()
        this.setState({...this.state, airpollution: event.target.value})
    }
    waterPollutionSelect=(event)=>{
        event.preventDefault()
        this.setState({...this.state, waterpollution: event.target.value})
    }
    lightPollutionSelect=(event)=>{
        event.preventDefault()
        this.setState({...this.state, lightpollution: event.target.value})
    }
    noisePollutionSelect=(event)=>{
        event.preventDefault()
        this.setState({...this.state, noisepollution: event.target.value})
    }
    footTrafficSelect=(event)=>{
        event.preventDefault()
        this.setState({...this.state, footTraffic: event.target.value})
    }
    ratings=[0,1,2,3,4,5]
    renderReviews=()=>{
        {return(this.state.reviews === false ?                 
        <div>
            <h1>{this.props.name}</h1>
            <h3>{this.props.location}</h3>
            <img src={this.props.picture} alt={this.props.name + ": " + this.props.location}/>
            <br></br>
            <button onClick={this.handleReview}>Reviews</button>
        </div>:
        <div>
            {this.state.showForm?
            <div>
                Create New Review
                <label for="airpollution">Air Pollution Levels:</label><br></br>
                <select name="rating" id="rating" onChange={this.airPollutionSelect}> 
                    <value>Choose One:</value>
                    {this.ratings.map(rating=><value value={rating}></value>)}
                </select><br></br>
                <label for="foottraffick">Foot Traffic Levels:</label><br></br>
                <select name="rating" id="rating" onChange={this.footTrafficSelect}> 
                    <value>Choose One:</value>
                    {this.ratings.map(rating=><value value={rating}></value>)}
                </select><br></br>
                <label for="waterpollution">Water Pollution Levels:</label><br></br>
                <select name="rating" id="rating" onChange={this.waterPollutionSelect}> 
                    <value>Choose One:</value><br></br>
                    {this.ratings.map(rating=><value value={rating}></value>)}
                </select><br></br>
                <label for="noisepollution">Noise Pollution Levels Levels:</label>
                <select name="rating" id="rating" onChange={this.noisePollutionSelect}> 
                    <value>Choose One:</value><br></br>
                    {this.ratings.map(rating=><value value={rating}></value>)}
                </select><br></br>
                <label for="lightpollution">Light Pollution Levels:</label><br></br>
                <select name="rating" id="rating" onChange={this.lightPollutionSelect}> 
                    <value>Choose One:</value>
                    {this.ratings.map(rating=><value value={rating}></value>)}
                </select><br></br>
                <input type="text-area" value={this.state.picture} onChange={this.imageURL} placeholder="Image URL"/>
                <input type="text-area" value={this.state.comment} onChange={this.commentText} placeholder="Comment"/>
                <input type="button" onClick={this.onSubmit} value="Submit" />
                <input type="button" onClick={this.showForm} value="Back"/>
                <input type="button" onClick={this.showForm} value="Show Review Form"/>
            </div>: null}
            <h1>Reviews</h1>
            {this.props.reviews.map(review=> <BeachReview 
            picture={review.picture}
            comment={review.comment}
            upvote={review.upvote}
            comment={review.comment}
            airpollution={review.airpollution}
            noisepollution={review.noisepollution}
            waterpollution={review.waterpollution}
            plasticpollution={review.plasticpollution}
            foottraffick={review.foottraffick}
            />)}
            <button onClick={this.handleReview}>Back</button>
        </div>
        )}
    }

    handleReview=(event)=>{
        event.preventDefault()
        this.state.reviews===false ?this.setState({...this.state, reviews: true}):  this.setState({...this.state, reviews: false})
    }
    render(){
        return(
            <div className="BeachDiv">
                {this.renderReviews()}
            </div>
        )
    }
}

export default BeachShow
