import React from 'react'

class BeachShow extends React.Component{
    render(){
        return(
            <div className="BeachDiv">
                <div>
                    <h1>{this.props.beach.name}</h1>
                    <h3>{this.props.beach.location}</h3>
                    <img src={this.props.beach.picture} alt={this.props.beach.name + ": " + this.props.beach.location}/>
                </div>
                    <button>Back</button>
            </div>
        )
    }
}

export default BeachShow
