import React from 'react'
import UserReview from './UserReview'

function sum(array){
    array.reduce((a, b) => a + b, 0)
}

class UserProfile extends React.Component{
    state={
        showReviews: false
    }

    showReviewsToggle=(event)=>{
        event.preventDefaul()
        this.state.showReviews === false ? this.setState({...this.state, showReviews: true}): this.setState({...this.state, showReviews: false})
    }
    render(){
        return(
            <div className="profile">
                <h3>{this.props.user.name}</h3>
                <h3>{this.props.user.username}</h3>
                <h5>Age: {this.props.user.age}</h5>
                <h5>Email: {this.props.user.email}</h5>
                <h5>Phone: {this.props.user.phone}</h5>
                <button>Edit Profile</button>
                <br></br>
                {this.props.currentUserReviews && this.state.showReviews? 
                    <div>
                    <h5>Number of Reviews:{this.props.currentUserReviews.length() === 0 && this.props.currentUserReviews? "You currently have no reviews": this.props.currentUserReviews.length()}</h5>
                    <h5>Total Upvotes: {sum(this.props.currentUserReviews.map(review => review.upvote))}</h5>
                    {this.currentUserReviews().map(review =><UserReview 
                        picture={review.picture}
                        comment={review.comment}
                        upvote={review.upvote}
                        comment={review.comment}
                        airpollution={review.airpollution}
                        noisepollution={review.noisepollution}
                        waterpollution={review.waterpollution}
                        plasticpollution={review.plasticpollution}
                        foottraffick={review.foottraffick}
                        />
                    )}
                </div>:<button>My Reviews</button>}

            </div>
        )

    }
}
export default UserProfile
