import React from 'react';
import './App.css';
import Nav from "./components/Nav"
import Home from "./components/Home"
import LoginSignup from './components/LoginSignup'


const userURL="http://localhost:3000/api/v1/users"
const beachURL="http://localhost:3000/api/v1/beachslots"
const reviewURL="http://localhost:3000/api/v1/beachreviews"

class App extends React.Component {
  state={
    currentUser:{},
    beaches:[],
    reviews:[],
    currentUserReviews:[],
    view: "about"
  }

  componentDidMount(){
    fetch(userURL)
    .then(r=>r.json())
    .then(data=>this.setState({...this.state, currentUser: data[0].user}))
    fetch(beachURL)
    .then(r=>r.json())
    .then(dataset=>dataset.map(data=>this.setState({...this.state, beaches:[...this.state.beaches, data.beach], currentUserReviews:[...this.state.reviews, data.beachreviews]})))
    fetch()
  }

  changeAbout=(event)=>{
    event.preventDefault()
    this.setState({...this.state, view: "about"})
  }

  changeBeach=(event)=>{
    event.preventDefault()
    this.setState({...this.state, view: "beaches"})
  }

  changeProfile=(event)=>{
    event.preventDefault()
    this.setState({...this.state, view: "profile"})
  }

  changeContact=(event)=>{
    event.preventDefault()
    this.setState({...this.state, view: "contact"})
  }

  change
  render(){
    return (
      <div className="App">
        <Nav 
        user={this.state.currentUser}
        view={this.state.view}
        changeAbout={this.changeAbout}
        changeBeach={this.changeBeach}
        changeProfile={this.changeProfile}
        changeContact={this.changeContact}/> 
        {this.state.currentUser==={}? <LoginSignup />:
        <Home 
        user={this.state.currentUser} 
        view={this.state.view}
        beaches={this.state.beaches}
        reviews={this.state.reviews}
        currentUserReviews={this.state.currentUserReviews}
        changeAbout={this.changeAbout}
        changeBeach={this.changeBeach}
        changeProfile={this.changeProfile}
        changeContact={this.changeContact}
        />}
      </div>
    );
  }
}

export default App;
