import React from 'react';
import './App.css';
import Nav from "./components/Nav"
import BeachIndex from "./components/BeachIndex"
import { Route, Switch } from 'react-router-dom';

const userURL="http://localhost:3000/api/v1/users"
const beachURL="http://localhost:3000/api/v1/beachslots"

class App extends React.Component {
  state={
    currentUser:{},
    beaches:[],
    reviews:[],
    currentUserReviews:[]
  }

  componentDidMount(){
    fetch(userURL)
    .then(r=>r.json())
    .then(data=>this.setState({...this.state, currentUser: data[0].user}))
    fetch(beachURL)
    .then(r=>r.json())
    .then(dataset=>dataset.map(data=>this.setState({...this.state, beaches:[...this.state.beaches, data.beach], currentUserReviews:[...this.state.reviews, data.beachreviews]})))
  }




  render(){
    return (
      <div className="App">
        <Nav /> {/** nav stays outside of our switch and routes b/c we want it visible all of the time  */}
        <Switch> {/** with switch, order from most to least specific and will only render ONE */}
          {/* <Route path="/login" render={(routerProps) => <Auth {...routerProps}/>} />
          <Route path="/help" render={() => <Help exampleCustomProp={false} />} />  */}
          {/** use render prop to route if you need custom props in your component */}
          {/* <Route path="/user/" component={UserProfile} />
          <Route path="/beaches/:id" component={BeachProfile} /> */}
          <Route exact path="/beaches" render={() => <BeachIndex beaches={this.state.beaches} reviews={this.state.reviews}/>} /> 
          {/* <Route exact path="/" component={Home} /> * will match if the user is a / or /anythingElse */}
        </Switch>
      </div>
    );
  }
}

export default App;
